import React, { useEffect, useState } from 'react';
import axios from '../API/Axios';
import requests from '../API/Request';
import './MovieList'; // Impo

const categories = [
  { title: 'Trending', fetchUrl: requests.fetchTrending },
  { title: 'Netflix Originals', fetchUrl: requests.fetchNetflixOriginals },
  { title: 'Top Rated', fetchUrl: requests.fetchTopRated },
  { title: 'Action Movies', fetchUrl: requests.fetchActionMovies },
  { title: 'Comedy Movies', fetchUrl: requests.fetchComedyMovies },
  { title: 'Horror Movies', fetchUrl: requests.fetchHorrorMovies },
  { title: 'Romance Movies', fetchUrl: requests.fetchRomanceMovies },
  { title: 'Documentaries', fetchUrl: requests.fetchDocumentaries },
];

function MovieList() {
  const [moviesByCategory, setMoviesByCategory] = useState({});

  useEffect(() => {
    async function fetchAll() {
      const results = {};
      for (const cat of categories) {
        try {
          const res = await axios.get(cat.fetchUrl);
          results[cat.title] = res.data.results || [];
        } catch (err) {
          results[cat.title] = [];
        }
      }
      setMoviesByCategory(results);
    }
    fetchAll();
  }, []);

  return (
    <div className="container">
      {categories.map((cat) => (
        <div key={cat.title} className="mb-4">
          <h3>{cat.title}</h3>
          <div className="row flex-nowrap overflow-auto">
            {moviesByCategory[cat.title]?.map((movie) => (
              <div key={movie.id} className="col-2 movie-item">
                <img
                  src={`https://image.tmdb.org/t/p/w300${movie.poster_path}`}
                  alt={movie.title || movie.name}
                  className="img-fluid rounded mb-2"
                />
                <div className="text-truncate">{movie.title || movie.name}</div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default MovieList;
