import React, { useEffect, useState } from 'react';
import axios from '../API/Axios';
import requests from '../API/Request';

function Banner() {
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    async function fetchData() {
      const res = await axios.get(requests.fetchTrending);
      const results = res.data.results;
      setMovie(results[Math.floor(Math.random() * results.length)]);
    }

    fetchData();
  }, []);

  return (
    <header
      className="banner d-flex align-items-end text-white"
      style={{
        height: '60vh',
        backgroundSize: 'cover',
        backgroundImage: `url("https://image.tmdb.org/t/p/original/${movie?.backdrop_path}")`,
        backgroundPosition: 'center center',
      }}
    >
      <div className="container pb-5">
        <h1 className="display-4 fw-bold">
          {movie?.title || movie?.name || movie?.original_name}
        </h1>
        <p className="lead col-md-8">{movie?.overview}</p>
        <div className="mt-3">
          <button className="btn btn-primary me-2">Play</button>
          <button className="btn btn-secondary">My List</button>
        </div>
      </div>
    </header>
  );
}

export default Banner;
