import React from 'react';
import './MovieDetail.css';

function MovieDetail({ movie, trailerUrl }) {
  if (!movie) return <div className="movie-detail">No movie selected.</div>;

  return (
    <div className="movie-detail d-flex bg-dark text-white p-4 rounded">
      <div className="flex-grow-1 me-4">
        <h2>{movie.title || movie.name}</h2>
        <hr className="bg-light"/>
        <div className="mb-2">
          <strong>Release Date:</strong> {movie.release_date}
        </div>
        <div className="mb-2">
          <strong>Vote:</strong> {movie.vote_average} / 10
        </div>
        <div>{movie.overview}</div>
      </div>
      <div style={{ minWidth: 350 }}>
        <iframe
          width="100%" height="400"
          src="https://www.youtube.com/embed/<key>"
        ></iframe>
      </div>
    </div>
  );
}

export default MovieDetail;
